  `user_qq` varchar(50) DEFAULT NULL COMMENT '用户QQ',
  `content` varchar(500) DEFAULT NULL COMMENT '留言内容',
  `add_time` varchar(50) DEFAULT NULL COMMENT '留言时间',
  `is_nm` int(2) DEFAULT '1' COMMENT '是否匿名，1是0否',
