  `reply_nickName` varchar(50) DEFAULT NULL COMMENT '回复人昵称',
  `reply_tx` varchar(300) DEFAULT NULL COMMENT '回复人头像',
  `reply_content` varchar(500) DEFAULT NULL COMMENT '回复内容',
  `is_used` int(2) DEFAULT '1' COMMENT '是否有效',
  PRIMARY KEY (`id`)
