/*Table structure for table `myalbum` */

DROP TABLE IF EXISTS `myalbum`;

CREATE TABLE `myalbum` (
  `album_id` int(10) NOT NULL AUTO_INCREMENT COMMENT 'id',
  `album_name` varchar(300) DEFAULT NULL COMMENT '名称',
  `album_description` varchar(500) DEFAULT NULL COMMENT '相册描述',
  `album_type` varchar(100) DEFAULT NULL COMMENT '类型',
  `album_role` int(2) DEFAULT NULL COMMENT '相册权限',
  `cover_url` varchar(300) DEFAULT NULL COMMENT '封面路径',
  `order_no` int(3) DEFAULT NULL COMMENT '排序号',
  `create_time` varchar(50) DEFAULT NULL COMMENT '创建时间',
  `update_time` varchar(50) DEFAULT NULL COMMENT '修改时间',
  `user_id` int(10) DEFAULT NULL COMMENT '用户id',
  `is_used` int(2) DEFAULT '1' COMMENT '是否有效',
  PRIMARY KEY (`album_id`)
) ENGINE=InnoDB AUTO_INCREMENT=4 DEFAULT CHARSET=utf8;

