/*Table structure for table `myalbum_photo` */

DROP TABLE IF EXISTS `myalbum_photo`;

CREATE TABLE `myalbum_photo` (
  `photo_id` int(10) NOT NULL AUTO_INCREMENT COMMENT 'id',
  `album_id` int(10) DEFAULT NULL COMMENT '所属相册 ',
