/*Table structure for table `person_info` */

DROP TABLE IF EXISTS `person_info`;

CREATE TABLE `person_info` (
  `id` int(10) NOT NULL AUTO_INCREMENT COMMENT 'id',
  `person_name` varchar(50) DEFAULT NULL COMMENT '性名',
  `person_sex` varchar(50) DEFAULT NULL COMMENT '性别',
  `person_hobby` varchar(50) DEFAULT NULL COMMENT '爱好',
  `person_address` varchar(250) DEFAULT NULL COMMENT '地址',
  `person_sign` varchar(1000) DEFAULT NULL COMMENT '个性签名',
  `person_explanin` varchar(1000) DEFAULT NULL COMMENT '个人说明',
  `person_work` varchar(50) DEFAULT NULL COMMENT '职业',
  `person_tel` varchar(50) DEFAULT NULL COMMENT '手机号码',
  `person_qq` varchar(50) DEFAULT NULL COMMENT 'qq号码',
  `person_tx` varchar(250) DEFAULT NULL COMMENT '头像地址',
  `person_others` varchar(1000) DEFAULT NULL COMMENT '其他',
  `user_id` int(10) DEFAULT NULL COMMENT '关联用户表',
  `is_used` int(2) DEFAULT '1' COMMENT '是否有效1有效，0无效',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8;

/*Data for the table `person_info` */

insert  into `person_info`(`id`,`person_name`,`person_sex`,`person_hobby`,`person_address`,`person_sign`,`person_explanin`,`person_work`,`person_tel`,`person_qq`,`person_tx`,`person_others`,`user_id`,`is_used`) values (1,'Liaoyuangui','男','上网','不知在何处','你想得到一般人不能得到的，你就必须先失去一般人不愿失去的。什么都想抓住，什么也抓不住。什么都想得到，什么也得不到。我们只有两只手，两只手只有十个指头，只有扔下一些东西，才能抓住更多更好的东西。只有停下一些事情，才能干成干好更多的事情。','从此赴往未卜的前途和叵恻的命运，以义无反顾的姿态彻底告别曾经。\n每天都在与世界纠缠着。人群时时刻刻在流动着，思维很少能停止下来。就像这白天黑夜的交替，分别与重逢，喜欢与淡漠，接受与排斥。\n二十年的时光，不知不觉就此别过，回想的日子，有些甜蜜，微微稍带些忧伤，想来想去，白驹过隙，浮生若梦，来来去去，离离散散... 一切的一切，都只是为了能让明天变得更好。','保密','18388668866','1129896968','/../PersonWebsitesUpload/userImg/fcf778bc-ae0f-4b10-b600-f93d4413d4ae.jpg',NULL,1,1);

/*Table structure for table `person_restinfo` */

DROP TABLE IF EXISTS `person_restinfo`;

CREATE TABLE `person_restinfo` (
  `id` int(10) NOT NULL AUTO_INCREMENT COMMENT '主键',
  `user_id` int(10) DEFAULT NULL COMMENT '关联用户的id',
  `my_lbImg1` varchar(300) DEFAULT NULL COMMENT '关于我的轮播图片',
  `my_lbImg2` varchar(300) DEFAULT NULL,
  `my_lbImg3` varchar(300) DEFAULT NULL,
  `my_lbImg4` varchar(300) DEFAULT NULL,
  `dt_addr` varchar(50) DEFAULT NULL COMMENT '地图定位地址',
  `addr_jd` varchar(100) DEFAULT NULL COMMENT '地址的精度',
  `addr_wd` varchar(100) DEFAULT NULL COMMENT '地址的维度',
  `jx_jj` varchar(3000) DEFAULT NULL COMMENT '家乡简介',
  `jx_lbImg1` varchar(300) DEFAULT NULL COMMENT '家乡轮播图片',
  `jx_lbImg2` varchar(300) DEFAULT NULL,
  `jx_lbImg3` varchar(300) DEFAULT NULL,
  `jx_lbImg4` varchar(300) DEFAULT NULL,
  `user_defined1` int(10) DEFAULT NULL COMMENT '自定义',
  `user_defined2` varchar(100) DEFAULT NULL,
  `user_defined3` varchar(100) DEFAULT NULL,
  `add_time` varchar(50) DEFAULT NULL COMMENT '添加时间',
  `update_time` varchar(50) DEFAULT NULL COMMENT '修改时间',
  `is_used` int(2) DEFAULT '1' COMMENT '是否有效',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=utf8;

/*Data for the table `person_restinfo` */

insert  into `person_restinfo`(`id`,`user_id`,`my_lbImg1`,`my_lbImg2`,`my_lbImg3`,`my_lbImg4`,`dt_addr`,`addr_jd`,`addr_wd`,`jx_jj`,`jx_lbImg1`,`jx_lbImg2`,`jx_lbImg3`,`jx_lbImg4`,`user_defined1`,`user_defined2`,`user_defined3`,`add_time`,`update_time`,`is_used`) values (1,1,'/PersonWebsites/../PersonWebsitesUpload/personRestInfo/b7f1323a-0b34-418f-91f2-713453f71ea4.jpg','/PersonWebsites/../PersonWebsitesUpload/personRestInfo/8f0b213b-062a-47a3-9695-62484af73633.jpg','/PersonWebsites/../PersonWebsitesUpload/personRestInfo/ffcf9daa-eb5d-4439-998e-f6b25c16fb77.jpg','/PersonWebsites/../PersonWebsitesUpload/personRestInfo/7b6ba8ab-6215-4c3a-bd63-96c6f71d86a4.jpg','正安','107.622312','28.574019','<p>正安县是贵州省遵义市下辖县，位于遵义市东北部，是贵州襟联重庆的前沿，是渝南、黔北经济文化的重要交汇区域，素有\"黔北门户\"之称。<p>\n<p>正安县位于贵州省北部，北接重庆市南川区，东北毗邻道真县、务川县，东南与凤冈县和湄潭县交界，南靠绥阳县，西北与桐梓县接壤。 </p>\n <p>正安县名优特产主要有野木瓜、正安白茶等，风景名胜主要有尹珍务本堂、九道水国家森林公园等。</p>','/PersonWebsites/../PersonWebsitesUpload/personRestInfo/d72892bc-87a0-45c1-ae74-234db0811b51.jpg','/PersonWebsites/../PersonWebsitesUpload/personRestInfo/6d43e931-dada-4cc5-8da5-17fe82d984d2.png','/PersonWebsites/../PersonWebsitesUpload/personRestInfo/9e3bac88-938f-4389-9fdd-c28a935739ca.jpg',NULL,NULL,NULL,NULL,'2017-11-03','2017-11-12',1),(2,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,1);

/*Table structure for table `praise` */

DROP TABLE IF EXISTS `praise`;

CREATE TABLE `praise` (
  `id` int(10) NOT NULL AUTO_INCREMENT COMMENT 'id',
