  `dy_id` int(10) DEFAULT NULL COMMENT '哪条动态',
  `praise_num` int(10) DEFAULT '0' COMMENT '点赞数',
  `is_used` int(2) DEFAULT '1' COMMENT '是否取消点赞',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

/*Data for the table `praise` */

/*Table structure for table `theme` */

DROP TABLE IF EXISTS `theme`;

CREATE TABLE `theme` (
  `id` int(10) NOT NULL AUTO_INCREMENT COMMENT '主键',
  `theme_name` varchar(50) DEFAULT NULL COMMENT '主题名',
  `head_imgUrl` varchar(300) DEFAULT NULL COMMENT '头部图片路径',
  `head_sign` varchar(500) DEFAULT NULL COMMENT '头部标签',
  `index_lbUrl1` varchar(300) DEFAULT NULL COMMENT '轮播图片1',
  `index_lbUrl2` varchar(300) DEFAULT NULL COMMENT '轮播图片2',
  `index_lbUrl3` varchar(300) DEFAULT NULL COMMENT '轮播图片3',
  `foot_label1` varchar(150) DEFAULT NULL COMMENT '底部标签1',
  `foot_label` varchar(150) DEFAULT NULL COMMENT '底部标签2',
  `user_defined1` int(10) DEFAULT NULL COMMENT '用户自定义',
  `user_defined2` varchar(300) DEFAULT NULL COMMENT '用户自定义',
  `user_defined3` varchar(300) DEFAULT NULL COMMENT '用户自定义',
  `add_time` varchar(50) DEFAULT NULL COMMENT '添加时间',
  `update_time` varchar(50) DEFAULT NULL COMMENT '修改时间',
  `is_start` int(2) DEFAULT '0' COMMENT '是否使用，1使用0禁用',
  `is_used` int(2) DEFAULT '1' COMMENT '是否有效',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=utf8;

/*Data for the table `theme` */

insert  into `theme`(`id`,`theme_name`,`head_imgUrl`,`head_sign`,`index_lbUrl1`,`index_lbUrl2`,`index_lbUrl3`,`foot_label1`,`foot_label`,`user_defined1`,`user_defined2`,`user_defined3`,`add_time`,`update_time`,`is_start`,`is_used`) values (1,'主题一','/../PersonWebsitesUpload/themeImg/9e522f6d-a8e7-4659-a1d0-73328f4732d7.png','风前欲劝春光住，春在城南芳草路。未随流落水边花，且作飘零泥上絮。 镜中已觉星星误，人不负春春自负。梦回人远许多愁，只在梨花风雨处。','/PersonWebsites/../PersonWebsitesUpload/themeImg/4e2bb8b4-c507-4b3d-b36b-170674f2489f.jpg','/PersonWebsites/../PersonWebsitesUpload/themeImg/bba83247-ea15-422e-aa3e-9fa9e2e1bbfd.jpg','/PersonWebsites/../PersonWebsitesUpload/themeImg/a1473acc-8143-4234-8341-9390505d8824.jpg','联系方式:1129696968@qq.com','The Boy Who Harnessed The Wind Liaoyuangui',NULL,NULL,NULL,'2017-11-12','2017-11-29',0,1),(2,'主题二','/../PersonWebsitesUpload/themeImg/f1df0cbd-5ade-40eb-b925-eb1fc9b9ccfe.png','不要在哀伤的诗句里告诉我：人生不过是一场幻梦！灵魂睡着了，就等于死了，事物的真相与外表不同。生是真切的！人生是实在的！它的归宿决不是荒坟；','/../PersonWebsitesUpload/themeImg/9b8e5711-eb02-4aa1-9b2d-1f95fa37e263.jpg','/../PersonWebsitesUpload/themeImg/f623aee2-8e25-4d60-9f4f-fa44db7867aa.jpg','/../PersonWebsitesUpload/themeImg/fb498a6b-cd80-443a-b703-0a63ab6a1800.jpg','联系方式:1129696968@qq.com','The Boy Who Harnessed The Wind Liaoyuangui',NULL,NULL,NULL,'2017-12-05','2017-12-05',1,1);

/*Table structure for table `user_role` */

DROP TABLE IF EXISTS `user_role`;

CREATE TABLE `user_role` (
  `role_id` int(2) NOT NULL AUTO_INCREMENT COMMENT 'id',
  `role_name` varchar(25) NOT NULL COMMENT '权限名',
  `role_power` int(2) NOT NULL COMMENT '权限',
  `is_used` int(2) NOT NULL COMMENT '是否有效',
  PRIMARY KEY (`role_id`)
) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=utf8;

/*Data for the table `user_role` */

insert  into `user_role`(`role_id`,`role_name`,`role_power`,`is_used`) values (1,'管理员',1,1),(2,'普通用户',2,1);

/*Table structure for table `users` */

DROP TABLE IF EXISTS `users`;

CREATE TABLE `users` (
  `id` int(10) NOT NULL AUTO_INCREMENT COMMENT 'id',
  `login_name` varchar(50) DEFAULT NULL COMMENT '登录名',
  `password` varchar(50) DEFAULT NULL COMMENT '密码',
  `phone` varchar(50) DEFAULT NULL COMMENT '手机号码',
  `email` varchar(50) DEFAULT NULL COMMENT 'email',
  `role_id` int(2) DEFAULT '2' COMMENT '权限1管理员2普通用户',
  `is_used` int(2) DEFAULT '1' COMMENT '是否有效，1有效0无效',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=8 DEFAULT CHARSET=utf8;

